import React,{useState}from 'react';
import {Link,Navigate,} from 'react-router-dom';
import axios from 'axios';
import { Header } from './components/Header';

const Login =  () => {
    const [auth,setAuth] = useState(false);
      const [data,setData] = useState({
        email: '',
        password: '',

    })
    const changeHandler = e =>{
        setData({...data,[e.target.name]:e.target.value})
    }
    const submitHandler = e =>{
        e.preventDefault();
        axios.post('http://localhost:5000/api/v1/auth/login',data) .then(
            res =>{localStorage.setItem('token',res.data.token);setAuth(true)}
        )   
    }
    if(auth){
        return <Navigate to="/dashboard" replace={true} />
    }
    return (
        <div>
            <Header/>
            <section className="container">
                <h1 className ="large text-primary">Sign In</h1>
                <p className="lead text-primary"><i className="fas fa-user"></i>Sign Into Your Account</p>
                <form className="form" onSubmit={submitHandler} autoComplete="off">
                    <div className="form-group">  
                    <input
                      type="email"
                      placeholder="Email Address"
                      name="email"
                      onChange={changeHandler}
                      required
                    />                     
                    </div>
                    <div className="form-group">   
                      <input
                        type="password" 
                        placeholder="Password"  
                        name="password"
                        onChange={changeHandler}
                      />                 
                    </div>
                    <input type="submit" className="btn btn-primary" value="Login"/>
                </form>
                <p className="my-l text-primary">
                    Don't have an account? <Link to="/register" className='text-primary'>Signup</Link>
                </p>
            </section>
        </div>
    )
}

export default Login
